const input = $input.first().json.body;

const chapterContent = input.chapter_content || '';
const chapterNumber = input.chapter_number || 1;
const novelTitle = input.novel_title || 'Untitled';
const genre = input.genre || 'romantic-comedy';
const characters = input.characters || [];
const previousSummary = input.previous_summary || '';

// Basic validation
const charCount = chapterContent.replace(/\s/g, '').length;
const MIN_CHARS = 4800;
const MAX_CHARS = 6000;

const systemPrompt = `คุณคือผู้เชี่ยวชาญด้าน Quality Control สำหรับนิยายไทย

หน้าที่ของคุณคือประเมินคุณภาพบทนิยายตามเกณฑ์ต่อไปนี้:

1. ความยาว (Length) - 20 คะแนน
   - ผ่านเกณฑ์ ${MIN_CHARS}-${MAX_CHARS} ตัวอักษร = 20 คะแนน
   - ขาด/เกินเล็กน้อย (±500) = 10 คะแนน
   - ขาด/เกินมาก = 0 คะแนน

2. ความต่อเนื่อง (Continuity) - 20 คะแนน
   - ต่อเนื่องจากตอนก่อนหน้าอย่างลื่นไหล
   - ไม่มีข้อขัดแย้งกับเหตุการณ์ที่ผ่านมา

3. คุณภาพการเขียน (Writing Quality) - 20 คะแนน
   - ภาษาสละสลวย
   - Show don't tell
   - บทสนทนาเป็นธรรมชาติ

4. ความสอดคล้องของตัวละคร (Character Consistency) - 20 คะแนน
   - บุคลิกตรงกับที่กำหนด
   - คำพูดเหมาะสมกับตัวละคร
   - การกระทำสมเหตุสมผล

5. Hook/Cliffhanger - 20 คะแนน
   - จบตอนด้วย hook ที่น่าสนใจ
   - ทำให้อยากอ่านต่อ`;

const userPrompt = `ประเมินคุณภาพบทนิยายต่อไปนี้:

## ข้อมูลเรื่อง:
- ชื่อเรื่อง: ${novelTitle}
- ตอนที่: ${chapterNumber}
- แนว: ${genre}

## ตัวละคร:
${JSON.stringify(characters, null, 2)}

## สรุปตอนที่แล้ว:
${previousSummary}

## เนื้อหาที่ต้องประเมิน:
${chapterContent}

## สถิติ:
- จำนวนตัวอักษร (ไม่รวม whitespace): ${charCount}
- เกณฑ์: ${MIN_CHARS}-${MAX_CHARS} ตัวอักษร

## คำสั่ง:
ให้คะแนนและความเห็นในรูปแบบ JSON:

\`\`\`json
{
  "scores": {
    "length": { "score": 0-20, "comment": "ความเห็น" },
    "continuity": { "score": 0-20, "comment": "ความเห็น" },
    "writing_quality": { "score": 0-20, "comment": "ความเห็น" },
    "character_consistency": { "score": 0-20, "comment": "ความเห็น" },
    "hook": { "score": 0-20, "comment": "ความเห็น" }
  },
  "total_score": 0-100,
  "pass": true/false,
  "overall_comment": "ความเห็นภาพรวม",
  "suggestions": ["ข้อเสนอแนะ 1", "ข้อเสนอแนะ 2"]
}
\`\`\`

เกณฑ์ผ่าน: 70 คะแนนขึ้นไป`;

return {
  novel_title: novelTitle,
  chapter_number: chapterNumber,
  character_count: charCount,
  meets_length_requirement: charCount >= MIN_CHARS && charCount <= MAX_CHARS,
  system_prompt: systemPrompt,
  user_prompt: userPrompt
};

const input = $input.first().json;
const content = input.text || input.content || '';

let qcResult = null;
let parseError = null;

try {
  const jsonMatch = content.match(/```json\s*([\s\S]*?)```/) || 
                    content.match(/\{[\s\S]*"scores"[\s\S]*\}/);

  if (jsonMatch) {
    const jsonStr = jsonMatch[1] || jsonMatch[0];
    qcResult = JSON.parse(jsonStr);
  } else {
    qcResult = JSON.parse(content);
  }
} catch (e) {
  parseError = e.message;
}

const prevData = $('Prepare QC Prompt').item.json;

return {
  success: qcResult !== null,
  novel_title: prevData.novel_title,
  chapter_number: prevData.chapter_number,
  character_count: prevData.character_count,
  meets_length_requirement: prevData.meets_length_requirement,
  qc_result: qcResult,
  parse_error: parseError,
  raw_response: content
};

parameters.responseBody:
={
  "success": false,
  "error": "Failed to parse QC result",
  "parse_error": "{{ $json.parse_error }}",
  "raw_response": {{ JSON.stringify($json.raw_response) }}
}
